package _1주;

public class _2일차_3Increment01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=5;
		x+=3;//x=x+3;
		System.out.println("x:"+x);//8
		x*=4;
		System.out.println("x:"+x);//32
		x%=5;
		System.out.println("x:"+x);//2
		x-=1;
		System.out.println("x:"+x);//1
		x+=7;
		System.out.println("x:"+x);//8
		
		int total=0;
		total+=1;
		total+=2;
		total+=3;
		total+=4;
		total+=5;
		total+=6;
		total+=7;
		total+=8;
		total+=9;
		total+=10;
		System.out.println("총합:"+total);//55
		
	}

}
